mapper.update:
update car_general
set attention=(#{attention} + 1)
where id = #{id};

mapper.select:
select c.id,c.car_name,c.car_price,c.model_level ,c.type ,i.url from car_general c left join image i
on c.img_id=i.iid


        c.type=#{type}


        and c.model_level=#{model_level}


        and c.bid=#{bid}

    and i.type=2

select attention
from car_general
where id = #{id}
